import './App.css';
import React,{useState,useEffect, useRef} from 'react';
import GrassGrid from './GrassGrid/GrassGrid';

function App() {

  const numRows = 20;
  const numCells = 80;

  const [position, setPosition] = useState({});
  const [isPositionUpdated, setIsPositionUpdated] = useState(false);

  const posRef = useRef(position);

  useEffect(() => {
    let coordX = Math.floor(80/2) - 1;
    let coordY = Math.floor(20/2) - 1;
    let objCoords = {
        x: coordX,
        y: coordY
    } 
    posRef.current = objCoords; 



    window.addEventListener('keyup', movementHandler)

  },[])

  const movementHandler = (direction) => {
    console.log('handler',posRef.current)
    let posX = posRef.current.x;
    let posY = posRef.current.y;
    let obj  = {}; 
    switch(direction.key){
        case 'ArrowUp':
            obj = {
                x: posX,
                y: posY - 1
            }
            posRef.current = obj;
            setPosition(obj)
        break;
        case 'ArrowDown':
            obj = {
                x: posX,
                y: posY + 1
            }
            posRef.current = obj;
            setPosition(obj)
        break;
        case 'ArrowLeft':
            obj = {
                x: posX - 1,
                y: posY
            }
            posRef.current = obj;
            setPosition(obj)
        break;
        case 'ArrowRight':
            obj = {
                x: posX + 1,
                y: posY
            }
            posRef.current = obj;
            setPosition(obj)
        break;
    }
  }

  useEffect(() => {
    console.log(position)
  },[position])
