import './App.css';
import React,{useState,useEffect} from 'react';
import GrassGrid from './GrassGrid/GrassGrid';

function App() {

  const numRows = 20;
  const numCells = 80;

  const [playerPosition, setPlayerPosition] = useState([]);

  useEffect(()=>
    {window.addEventListener('keyup', (event) => {
      console.log(event.key);
      switch (event.key){
          case 'ArrowUp': movePlayer('Up', event); break;
          case 'ArrowDown': movePlayer('Down', event); break;
          case 'ArrowLeft': movePlayer('Left', event); break;
          case 'ArrowRight': movePlayer('Right', event); break;
      }
    })
    let calcX = Math.floor(numCells / 2) - 1
    let calcY = Math.floor(numRows / 2) - 1
    setPlayerPosition({x: calcX,y: calcY});
  },[])

  function movePlayer(direction, event){
    console.log('direction: ',direction)
    let playerPositionCopy = playerPosition;
    console.log('playerPosition: ',playerPosition);
    switch(direction){
      case 'Up':
          setPlayerPosition({x: playerPositionCopy.x, y: playerPositionCopy.y - 1})
        break;
      case 'Down':
          setPlayerPosition({x: playerPositionCopy.x, y: playerPositionCopy.y + 1})
        break;
      case 'Left':
          setPlayerPosition({x: playerPositionCopy.x - 1, y: playerPositionCopy.y})
        break;
      case 'Right':
          setPlayerPosition({x: playerPositionCopy.x + 1, y: playerPositionCopy.y})
        break;
    }

  }

  return (
    <div>
      <div className="App">
        <header className="App-header">
            <GrassGrid 
              x = {numCells} 
              y = {numRows} 
              playerPosition = {playerPosition}>
            </GrassGrid>
        </header>
      </div>
    </div>
  );
}

export default App;
