import './Pause.css';
import React from 'react'
import PauseMenuImage from '../assets/pausemenu.png';
import SelectedMenuImage from '../assets/arrowselect.gif';
import startMenuAudio from "../music/startmenu.mp3";
import { useState, useEffect, useRef } from 'react';

function Pause(props) {

    const [audioStartMenu] = useState(new Audio(startMenuAudio));

    const [selectedMenu, setSelectedMenu] = useState(0);
    const selectedRef = useRef(selectedMenu);

    const [pauseArray] = useState("Pokédex","Pokémon","Borsa","AiGrading","Salva","Opzioni","Esci")

    useEffect(() =>{
        window.addEventListener('keyup', pauseHandler);
        window.addEventListener('keyup', pauseMenuSelectHandler);

        return () => {
            window.removeEventListener('keyup', pauseHandler);
            window.removeEventListener('keyup', pauseMenuSelectHandler);
        };
    },[])

    const pauseHandler = (key) => {
        if(key.key !== 'Escape') return;
        props.inPauseRef.current = !props.inPauseRef.current;
        props.setIsInPause(props.inPauseRef.current);
        playAudioStartMenu();
    }

    const pauseMenuSelectHandler = (direction) => {
        if(direction.key !== 'ArrowUp' && direction.key !== 'ArrowDown') return;
        switch(direction.key){
            case 'ArrowUp':
                selectedRef.current--;
            break;
            case 'ArrowDown':
                selectedRef.current++;
            break;
        }
        if(selectedRef.current >= pauseArray.length) selectedRef.current = pauseArray.length - 1;
        if(selectedRef.current < 0) selectedRef.current = 0;
        setSelectedMenu(selectedRef.current);
        console.log(selectedRef);
        
    }

    const playAudioStartMenu = () => {
        audioStartMenu.pause();
        audioStartMenu.currentTime = 0;
        audioStartMenu.play();
        audioStartMenu.volume = 0.05;
    }

    return (
        <div>
            <div className={props.inPauseRef.current ? 'inpauseText' : 'noinpauseText'}>Pause</div>
            <div className={props.inPauseRef.current ? 'inpauseBox' : 'noinpauseBox'}>
                <div className='row'>
                    <div className="col">
                        <img className={selectedMenu === 0 ? 'selected' : 'noselected'} alt='selected' src={SelectedMenuImage}></img> 
                        Pokédex
                    </div>
                </div>
                <div className="row">
                    <div className="col">
                        <img className={selectedMenu === 1 ? 'selected' : 'noselected'} alt='selected' src={SelectedMenuImage}></img> 
                        Pokémon
                    </div>
                </div>
                <div className="row">
                    <div className="col">
                        <img className={selectedMenu === 2 ? 'selected' : 'noselected'} alt='selected' src={SelectedMenuImage}></img> 
                        Borsa
                    </div>
                </div>
                <div className="row">
                    <div className="col">
                        <img className={selectedMenu === 3 ? 'selected' : 'noselected'} alt='selected' src={SelectedMenuImage}></img> 
                        AiGrading
                    </div>
                </div>
                <div className="row">
                    <div className="col">
                        <img className={selectedMenu === 4 ? 'selected' : 'noselected'} alt='selected' src={SelectedMenuImage}></img> 
                        Salva
                    </div>
                </div>
                <div className="row">
                    <div className="col">
                        <img className={selectedMenu === 5 ? 'selected' : 'noselected'} alt='selected' src={SelectedMenuImage}></img> 
                        Opzioni
                    </div>
                </div>
                <div className="row">
                    <div className="col">
                        <img className={selectedMenu === 6 ? 'selected' : 'noselected'} alt='selected' src={SelectedMenuImage}></img> 
                        Exit
                    </div>
                </div>
            </div>  
            <img className={props.inPauseRef.current ? 'inpauseMenu' : 'noinpauseMenu'} alt='imagePause' src={PauseMenuImage}></img>
        </div>
    )
}

export default Pause
