import React, { useEffect, useState } from 'react'
import erba from './assets/erba.png'
import logo from './assets/logoAiGrading.png'
import './CreateTable.css';

function CreateTable(rows, columns) {
    const [playerPosition, setPlayerPosition] = useState();

    useEffect(()=>{
      const tableRows = [];
      for (let x = 0; x < rows; x++) {
        const rowCells = [];
    
        for (let y = 0; y < columns; y++) {
          rowCells.push(<td className='erba' key={y}><div className='noheight'><img src={erba}/></div></td>);
        }
    
        tableRows.push(<tr key={x}>{rowCells}</tr>);
      }
    },[])
    
    function setPlayerCenterPosition(x,y){
      setPlayerPosition([x,y])
    }

    // Calculate the center coordinates
    const centerRow = Math.floor(rows / 2) - 1;
    const centerColumn = Math.floor(columns / 2) - 1;

    if (tableRows[centerRow] && tableRows[centerRow].props.children[centerColumn]) {
        setPlayerCenterPosition(centerRow,centerColumn)
        tableRows[centerRow].props.children[centerColumn] = (
          <td className='erba' key={centerColumn}>
            <div className='noheight'>
                <img className='tile' src={logo} alt="pg" />
            </div>
          </td>
        );
    }
    

    return (
      <table className="table">
        <tbody>
          {tableRows}
        </tbody>
      </table>
    );
}

export default CreateTable
