import './Pause.css';
import React from 'react'
import PauseMenuImage from '../assets/pausemenu.png';
import SelectedMenuImage from '../assets/arrowselect.gif';
import startMenuAudio from "../music/startmenu.mp3";
import { useState, useEffect, useRef } from 'react';

function Pause(props) {

    const [audioStartMenu] = useState(new Audio(startMenuAudio));

    const [selectedMenu, setSelectedMenu] = useState(0);
    const selectedRef = useRef(selectedMenu);

    const [pauseArray] = useState(["Pokédex","Pokémon","Borsa","AiGrading","Salva","Opzioni","Esci"])

    useEffect(() =>{
        window.addEventListener('keyup', pauseHandler);
        window.addEventListener('keyup', pauseMenuSelectHandler);

        return () => {
            window.removeEventListener('keyup', pauseHandler);
            window.removeEventListener('keyup', pauseMenuSelectHandler);
        };
    },[])

    const pauseHandler = (key) => {
        if(key.key !== 'Escape') return;
        props.pauseFunction();
    }

    const pauseMenuSelectHandler = (direction) => {
        if(direction.key !== 'ArrowUp' && direction.key !== 'ArrowDown') return;
        switch(direction.key){
            case 'ArrowUp':
                selectedRef.current--;
            break;
            case 'ArrowDown':
                selectedRef.current++;
            break;
        }
        if(selectedRef.current >= pauseArray.length) selectedRef.current = 0;
        if(selectedRef.current < 0) selectedRef.current = pauseArray.length - 1;
        setSelectedMenu(selectedRef.current);        
    }

    const playAudioStartMenu = () => {
        audioStartMenu.pause();
        audioStartMenu.currentTime = 0;
        audioStartMenu.play();
        audioStartMenu.volume = 0.05;
    }

    return (
        <div>
            <div className={props.inPauseRef.current ? 'inpauseText' : 'noinpauseText'}>Pausa</div>
            <div className={props.inPauseRef.current ? 'inpauseBox' : 'noinpauseBox'}>
                {pauseArray.map((element, index) => {
                    return(
                    <div className='row' key={index}>
                        <div className="col">
                            <img className={selectedMenu === index ? 'selected' : 'noselected'} alt='selected' src={SelectedMenuImage}></img> 
                            {element}
                        </div>
                    </div>
                    );
                })}
            </div>  
            <img className={props.inPauseRef.current ? 'inpauseMenu' : 'noinpauseMenu'} alt='imagePause' src={PauseMenuImage}></img>
        </div>
    )
}

export default Pause
