import './App.css';
import React,{useState,useEffect} from 'react';
import GrassGrid from './GrassGrid/GrassGrid';

function App() {

  const numRows = 20;
  const numCells = 80;

  const [MovementString, setMovementString] = useState('Stop')

  const [playerPosition, setPlayerPosition] = useState([]);

  useEffect(()=>{
    let calcX = Math.floor(numCells / 2) - 1
    let calcY = Math.floor(numRows / 2) - 1
    setPlayerPosition({x: calcX,y: calcY});
  },[])

  window.addEventListener('keydown', (event) => {
    switch (event.key){
        case 'ArrowUp': setMovementString('Up'); break;
        case 'ArrowDown': setMovementString('Down'); break;
        case 'ArrowLeft': setMovementString('Left'); break;
        case 'ArrowRight': setMovementString('Right'); break;
    }
  })


  return (
    <div>
      <div className="App">
        <header className="App-header">
            <GrassGrid x = {playerPosition.x}></GrassGrid>
        </header>
      </div>
    </div>
  );
}

export default App;
