import './Pause.css';
import React from 'react'
import PauseMenuImage from '../assets/pausemenu.png';
import SelectedMenuImage from '../assets/arrowselect.gif';
import startMenuAudio from "../music/startmenu.mp3";
import { useState, useEffect } from 'react';

function Pause(props) {

    const [audioStartMenu] = useState(new Audio(startMenuAudio));

    const [selectedMenu, setSelectedMenu] = useState(0)

    useEffect(() =>{
        window.addEventListener('keyup', pauseHandler);

        return () => {
            window.removeEventListener('keyup', pauseHandler);
        };
    },[])

    const pauseHandler = (key) => {
        if(key.key !== 'Escape') return;
        props.inPauseRef.current = !props.inPauseRef.current;
        props.setIsInPause(props.inPauseRef.current);
        playAudioStartMenu();
    }

    const playAudioStartMenu = () => {
        audioStartMenu.pause();
        audioStartMenu.currentTime = 0;
        audioStartMenu.play();
        audioStartMenu.volume = 0.05;
    }

    return (
        <div>
            <div className={props.inPauseRef.current ? 'inpauseText' : 'noinpauseText'}>Pause</div>
            <div className={props.inPauseRef.current ? 'inpauseBox' : 'noinpauseBox'}>
                <div className='row'>
                    <div class="col-sm-1">
                        <img className={selectedMenu == 0 ? 'selected' : 'noselected'} alt='selected' src={SelectedMenuImage}></img> 
                        Pokédex
                    </div>
                </div>
                <div className="row">
                    <div class="col-sm">
                        <img className={selectedMenu == 1 ? 'selected' : 'noselected'} alt='selected' src={SelectedMenuImage}></img> 
                        Pokémon
                    </div>
                </div>
                <div className="row">
                    Borsa
                </div>
                <div className="row">
                    AiGrading
                </div>
                <div className="row">
                    Salva
                </div>
                <div className="row">
                    Opzioni
                </div>
                <div className="row">
                    Exit
                </div>
            </div>  
            <img className={props.inPauseRef.current ? 'inpauseMenu' : 'noinpauseMenu'} alt='imagePause' src={PauseMenuImage}></img>
        </div>
    )
}

export default Pause
