import './Pause.css';
import React from 'react'
import PauseMenuImage from '../assets/pausemenu.png';
import startMenuAudio from "../music/startmenu.mp3";
import { useState, useEffect } from 'react';

function Pause(props) {
    
    const [audioStartMenu, setAudioStartMenu] = useState(new Audio(startMenuAudio));

    useEffect(() =>{
        playAudioStartMenu();
        console.log("passo");
    },[props.isInPause])

    const playAudioStartMenu = () => {
        audioStartMenu.pause();
        audioStartMenu.currentTime = 0;
        audioStartMenu.play();
        audioStartMenu.volume = 0.05;
    }

    return (
        <div>
            <div>{props.test}</div>
            <img className={props.isInPause ? 'inpauseMenu' : 'noinpauseMenu'} src={PauseMenuImage}></img>
        </div>
    )
}

export default Pause
