import './App.css';
import React,{useState,useEffect, useCallback, useRef} from 'react';
import GrassGrid from './GrassGrid/GrassGrid';

function App() {

  const numRows = 20;
  const numCells = 80;
  let calcX = Math.floor(numCells / 2) - 1;
  let calcY = Math.floor(numRows / 2) - 1;

  const playerPositionX = useRef(calcX);
  const playerPositionY = useRef(calcY);

  useEffect(()=>{

    const movePlayer = (event) => {
      console.log(event.key);
      switch (event.key){
        case 'ArrowUp':
          playerPositionX.current = playerPositionX.current;
          playerPositionY.current = playerPositionY.current - 1;
          break;
          /*
        case 'ArrowDown': 
          setPlayerPositionX(playerPositionX)
          setPlayerPositionY(playerPositionY+1) 
          break;
        case 'ArrowLeft': 
          setPlayerPositionX(playerPositionX-1)
          setPlayerPositionY(playerPositionY)
          break;
        case 'ArrowRight': 
          setPlayerPositionX(playerPositionX+1)
          setPlayerPositionY(playerPositionY)
          break;
          */
      }
    };

    window.addEventListener('keyup', movePlayer);
    return () => {
      window.removeEventListener('keyup', movePlayer);
    };
  },[])



  return (
    <div>
      <div className="App">
        <header className="App-header">
            <GrassGrid 
              x = {numCells} 
              y = {numRows} 
              playerPosition = {{x: playerPositionX.current, y: playerPositionY.current}}>
            </GrassGrid>
        </header>
      </div>
    </div>
  );
}

export default App;
