import './Pause.css';
import React from 'react'
import PauseMenuImage from '../assets/pausemenu.png';
import startMenuAudio from "../music/startmenu.mp3";
import { useState, useEffect, useRef } from 'react';

function Pause(props) {

    const [fixSetter, setFixSetter] = useState(false);
    
    const fixSetterRef = useRef(fixSetter);
    
    const [audioStartMenu, setAudioStartMenu] = useState(new Audio(startMenuAudio));

    useEffect(() =>{
        console.log("fixSetter: "+fixSetter);
        if(fixSetter) return;
        fixSetterRef.current = true;
        setFixSetter(true);
        window.addEventListener('keyup', pauseHandler);
    },[])

    const pauseHandler = (key) => {
        if(key.key != 'Escape') return;
        if(props.isInBattle) return;
        props.inPauseRef.current = !props.inPauseRef.current;
        props.setIsInPause(props.inPauseRef.current);
        playAudioStartMenu();
    }

    const playAudioStartMenu = () => {
        audioStartMenu.pause();
        audioStartMenu.currentTime = 0;
        audioStartMenu.play();
        audioStartMenu.volume = 0.05;
    }

    return (
        <div>
            <img className={props.inPauseRef.current ? 'inpauseMenu' : 'noinpauseMenu'} src={PauseMenuImage}></img>
        </div>
    )
}

export default Pause
