import './App.css';
import React,{useState,useEffect} from 'react';
import GrassGrid from './GrassGrid/GrassGrid';

function App() {

  const numRows = 20;
  const numCells = 80;
  let calcX = Math.floor(numCells / 2) - 1;
  let calcY = Math.floor(numRows / 2) - 1;

  const [playerPositionX, setPlayerPositionX] = useState(calcX);
  const [playerPositionY, setPlayerPositionY] = useState(calcY);

  function movePlayer(direction,event){
    console.log(calcX)
    console.log('playerPositionX: ',playerPositionX);
    console.log('playerPositionY: ',playerPositionY);
    switch(direction){
      case 'Up':
          setPlayerPositionX(playerPositionX)
          setPlayerPositionY(playerPositionY-1)
        break;
      case 'Down':
          setPlayerPositionX(playerPositionX)
          setPlayerPositionY(playerPositionY+1)
        break;
      case 'Left':
          setPlayerPositionX(playerPositionX-1)
          setPlayerPositionY(playerPositionY)
        break;
      case 'Right':
          setPlayerPositionX(playerPositionX+1)
          setPlayerPositionY(playerPositionY)
        break;
    }
    document.removeEventListener('keyup', event);
  }

  useEffect(()=>{
    console.log('inizio')
    window.addEventListener('keyup', (event) => {
      switch (event.key){
          case 'ArrowUp': movePlayer('Up',event); break;
          case 'ArrowDown': movePlayer('Down'); break;
          case 'ArrowLeft': movePlayer('Left'); break;
          case 'ArrowRight': movePlayer('Right'); break;
      }
    })
  },[])



  return (
    <div>
      <div className="App">
        <header className="App-header">
            <GrassGrid 
              x = {numCells} 
              y = {numRows} 
              playerPosition = {{x: playerPositionX, y: playerPositionY}}>
            </GrassGrid>
        </header>
      </div>
    </div>
  );
}

export default App;
