import React from 'react'
import { useEffect } from 'react';

function Pause(props) {

    useEffect(() =>{
        window.addEventListener('keyup', pauseHandler)
    },[])

    const pauseHandler = (key) => {
        if(key.key != 'Escape') return;
        inPauseRef.current = !inPauseRef.current;
        setIsInPause(inPauseRef.current);
        playAudioStartMenu();
    }

    return (
        <img className={props.isInPause ? 'inpauseMenu' : 'noinpauseMenu'} src={PauseMenuImage}></img>
    )
}

export default Pause
